body {
  margin: 0;
  padding: 0;
  height: 100vh;
  width: 100%;
  background: #18181b;
  display: flex;
  align-items: center;
  justify-content: center;
}

.hero-text {
  color: #fff;
  font-size: 3rem;
  text-align: center;
}

.trailer {
  position: fixed;
  top: 0;
  left: 0;
  transform: translate(-50%, -50%);
  border-radius: 50%;
  background-color: #7dd3fc;
  mix-blend-mode: difference;
  pointer-events: none;
  transition: height 0.3s ease-out, width 0.3s ease-out, filter 0.3s ease-out;
}

const trailer = document.getElementById("trailer");
const heroText = document.getElementById("heroText");

let isHovered = false;

document.addEventListener("mousemove", (e) => {
  const { clientX, clientY } = e;
  const size = isHovered ? 300 : 30;

  trailer.animate(
    {
      left: `${clientX}px`,
      top: `${clientY}px`,
    },
    { duration: 500, fill: "forwards" }
  );

  trailer.style.height = `${size}px`;
  trailer.style.width = `${size}px`;
  trailer.style.filter = `blur(${isHovered ? 30 : 0}px)`;
});

heroText.addEventListener("mouseenter", () => (isHovered = true));
heroText.addEventListener("mouseleave", () => (isHovered = false));
