* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  background-color: #18181b;
}

#morph-navbar {
  position: relative;
  margin-inline: auto;
  display: flex;
  height: 3.5rem;
  max-width: fit-content;
  border-radius: 9999px;
  background: linear-gradient(180deg, #404040, #262626);
  padding: 0.25rem;
  color: white;
  box-shadow:
    0 8px 32px rgba(0, 0, 0, 0.6),
    0 2px 8px rgba(0, 0, 0, 0.4),
    inset 0 1px 0 rgba(255, 255, 255, 0.1),
    inset 0 -1px 2px rgba(0, 0, 0, 0.5);
}

#morph-navbar-container {
  position: relative;
  display: flex;
  height: 100%;
  width: 100%;
  column-gap: 0.25rem;
  border-radius: 9999px;
  background: linear-gradient(180deg, #262626, #171717);
  padding: 0.25rem;
  box-shadow:
    inset 0 2px 4px rgba(0, 0, 0, 0.8),
    inset 0 -1px 2px rgba(255, 255, 255, 0.05);
}

.morph-navbar-item {
  position: relative;
  z-index: 20;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 9999px;
  padding: 0.75rem 1rem;
  font-size: 0.75rem;
  line-height: 1.33;
  font-weight: 500;
  font-family: "Inter", sans-serif;
  text-decoration: none;
  color: white;
  transition: color 0.2s ease;
}

.morph-navbar-item.is-active {
  color: #171717;
}

.active-pill {
  position: absolute;
  top: 4px;
  z-index: 5;
  height: 2.5rem;
  border-radius: 9999px;
  background: linear-gradient(180deg, #e8e8e8, #c0c0c0, #a0a0a0);
  box-shadow:
    0 2px 8px rgba(0, 0, 0, 0.4),
    0 1px 2px rgba(0, 0, 0, 0.3),
    inset 0 2px 0 rgba(255, 255, 255, 0.5),
    inset 0 -2px 0 rgba(255, 255, 255, 0.3),
    inset 0 -8px 16px rgba(255, 255, 255, 0.2),
    inset 0 0 0 1px rgba(255, 255, 255, 0.4),
    inset 0 1px 4px rgba(0, 0, 0, 0.1);
}

.hover-pill {
  position: absolute;
  height: 2.5rem;
  border-radius: 9999px;
  background: linear-gradient(
    to bottom,
    rgba(255, 255, 255, 0.1),
    rgba(255, 255, 255, 0.05)
  );
  box-shadow: inset 0 1px 2px rgba(255, 255, 255, 0.2);
  backdrop-filter: blur(8px);
}

@media (min-width: 768px) {
  #morph-navbar {
    height: 4rem;
  }

  .morph-navbar-item {
    padding: 0.75rem 1.75rem;
    font-size: 1rem;
    line-height: 1.5;
  }

  .active-pill {
    height: 3rem;
  }

  .hover-pill {
    height: 3rem;
  }
}

import { animate } from "motion";

const navbar = document.getElementById("morph-navbar-container");
const items = Array.from(document.querySelectorAll(".morph-navbar-item"));

const activePill = document.createElement("span");
activePill.className = "active-pill";

const hoverPill = document.createElement("span");
hoverPill.className = "hover-pill";

navbar.appendChild(activePill);
navbar.appendChild(hoverPill);

const PADDING_OFFSET = 4;

function getRelativeRect(el) {
  const elRect = el.getBoundingClientRect();
  const containerRect = navbar.getBoundingClientRect();

  return {
    left: elRect.left - containerRect.left,
    width: elRect.width,
  };
}

function normalizeHref(href) {
  try {
    const url = new URL(href, window.location.origin);
    return url.pathname + url.hash;
  } catch {
    return href;
  }
}

function getActiveItemFromURL() {
  const current = window.location.pathname + window.location.hash;

  return items.find((item) => {
    const itemHref = normalizeHref(item.getAttribute("href"));
    return current === itemHref || window.location.hash === itemHref;
  });
}

function moveActivePill(el, immediate = false) {
  if (!el) return;

  const { left, width } = getRelativeRect(el);

  el.classList.add("is-active");
  items.forEach((i) => i !== el && i.classList.remove("is-active"));

  animate(
    activePill,
    { left: `${left}px`, width: `${width}px` },
    immediate
      ? { duration: 0 }
      : { type: "spring", stiffness: 400, damping: 33 },
  );
}

function moveHoverPill(el) {
  const { left, width } = getRelativeRect(el);

  animate(
    hoverPill,
    { left: `${left}px`, width: `${width}px` },
    { type: "spring", stiffness: 400, damping: 33 },
  );
}

function resetHoverPill() {
  animate(
    hoverPill,
    {
      left: `${PADDING_OFFSET}px`,
      width: `calc(100% - ${PADDING_OFFSET * 2}px)`,
    },
    { type: "spring", stiffness: 400, damping: 33 },
  );
}

items.forEach((item) => {
  item.addEventListener("mouseenter", () => moveHoverPill(item));

  item.addEventListener("mouseleave", resetHoverPill);

  item.addEventListener("click", () => {
    moveActivePill(item);
  });
});

window.addEventListener("load", () => {
  const activeItem = getActiveItemFromURL();

  if (activeItem) {
    moveActivePill(activeItem, true);
  } else if (items[0]) {
    moveActivePill(items[0], true);
  }

  resetHoverPill();
});
