body {
  margin: 0;
  padding: 0;
  min-height: 100vh;
  width: 100%;
  background: #18181b;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: #fff;
}

.spacer {
  height: 100vh;
}

.reveal-text {
  font-size: 3rem;
  font-weight: bold;
  text-align: center;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  max-width: 64rem;
}

.word {
  display: flex;
  align-items: center;
  margin-right: 0.5rem;
}

.character-container {
  position: relative;
  display: inline-flex;
}

.char-shadow {
  position: absolute;
  inset: 0;
  pointer-events: none;
  opacity: 0.1;
}

.char {
  opacity: 0;
}

import { animate, scroll, transform } from "motion";

const revealText = document.getElementById("reveal-text");

const words = revealText.innerText.split(" ");
revealText.innerHTML = words
  .map(
    (word) => `
    <span class="word">
      ${word
        .split("")
        .map(
          (char) => `
          <span class="character-container">
            <span class="char-shadow">${char}</span>
            <span class="char">${char}</span>
          </span>
        `
        )
        .join("")}
    </span>
  `
  )
  .join(" ");

const characters = document.querySelectorAll(".char");

characters.forEach((char) => {
  char.style.opacity = 0;
});

scroll(
  (progress) => {
    characters.forEach((char, i) => {
      const start = i / characters.length;
      const end = (start + i) / characters.length;

      const opacity = transform(progress, [start, end], [0, 1]);

      char.style.opacity = opacity;
    });
  },
  {
    target: revealText,
    offset: ["start 0.8", "start 0.3"],
  }
);
