body {
  margin: 0;
  padding: 0;
  min-height: 100vh;
  width: 100%;
  background: #18181b;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: #fff;
}

.spacer {
  height: 100vh;
}

.reveal-text {
  font-size: 3rem;
  font-weight: bold;
  text-align: center;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 0.3rem;
  max-width: 64rem;
}

.word-container {
  position: relative;
  display: inline-block;
}

.word-shadow {
  font-size: 3rem;
  font-weight: bold;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  opacity: 0.1;
  pointer-events: none;
}

.word {
  font-size: 3rem;
  font-weight: bold;
  opacity: 0;
  display: inline-block;
}

import { animate, scroll, transform } from "motion";

const revealText = document.getElementById("reveal-text");

const words = revealText.innerText.split(" ");
revealText.innerHTML = words
  .map(
    (word) => `
    <span class="word-container">
      <span class="word-shadow">${word}</span>
      <span class="word">${word}</span>
    </span>
  `
  )
  .join(" ");

const wordSpans = document.querySelectorAll(".word");

wordSpans.forEach((span) => {
  span.style.opacity = 0;
});

scroll(
  (progress) => {
    wordSpans.forEach((span, i) => {
      const start = i / wordSpans.length;
      const end = (start + i) / wordSpans.length;

      const opacity = transform(progress, [start, end], [0, 1]);

      span.style.opacity = opacity;
    });
  },
  {
    target: revealText,
    offset: ["start 0.8", "start 0.3"],
  }
);
